def target_score(y_true, predictions):
    return (auc_score(y_true == 1, predictions) + auc_score(y_true == -1, -predictions)) / 2


def parse_dataframe(filename):
    df = pd.read_csv(filename)
    parse_cell = lambda cell: np.fromstring(cell, dtype=np.float, sep=" ")
    df = df.applymap(parse_cell)
    return df


def combine_data_and_types(df_data, df_types):
    assert isinstance(df_data, pd.DataFrame)
    assert isinstance(df_types, pd.DataFrame)
    assert df_data.shape[0] == df_types.shape[0]
    return pd.DataFrame([df_data['A'], df_data['B'], df_types['A type'], df_types['B type']]).T


def write_submission(valid_path, predictions):
    submission_path = valid_path.replace("pairs", "submission")
    submission_path = SETTINGS.FC_TRAIN.SUBMISSION
    print("Writing submission file: {}".format(submission_path))
    writer = csv_writer(open(submission_path, "w"), lineterminator="\n")
    valid = parse_dataframe(valid_path)
    rows = [x for x in zip(valid.index, predictions)]
    writer.writerow(("SampleID", "Target"))
    writer.writerows(rows)


def predict_proba(clf, data):
    assert np.all(clf.classes_ == np.array([-1, 0, 1]))
    probabilities = clf.predict_proba(data)
    return probabilities[:, 2] - probabilities[:, 0]


def output_summary(y_true, predictions, publicinfo):
    print("total error:\t{}".format(target_score(y_true, predictions)))
    indices = defaultdict(list)
    for idx in range(publicinfo.shape[0]):
        key = tuple(sorted([publicinfo['A type'][idx], publicinfo['B type'][idx]]))
        indices[key].append(idx)
    for key, idx in indices.items():
        print("{}:\t{}".format(key, target_score(y_true[idx], predictions[idx])))


def get_df(filename):
    data = parse_dataframe(filename)
    types = pd.read_csv(filename.replace("pairs", "publicinfo"))
    return combine_data_and_types(data, types)


def double_data(df):
    tmp = 0 * deepcopy(df)
    for col_name in df.columns:
        if "A->B" in col_name:
            tmp[col_name.replace("A->B", "B->A")] = df[col_name]
        elif "B->A" in col_name:
            tmp[col_name.replace("B->A", "A->B")] = df[col_name]
        elif "difference" in col_name:
            tmp[col_name] = -df[col_name]
        else:
            raise Exception
    big_df = pd.concat((df, tmp))
    big_df.index = range(big_df.shape[0])
    return big_df


def double_original_data(df):
    tmp = 0 * deepcopy(df)
    for col_name in df.columns:
        if "A" in col_name:
            tmp[col_name.replace("A", "B")] = df[col_name]
        elif "B" in col_name:
            tmp[col_name.replace("B", "A")] = df[col_name]
        else:
            raise Exception
    big_df = pd.concat((df, tmp))
    big_df.index = range(big_df.shape[0])
    return big_df

